class AuthService {

  login(username, password, tenant) {

	console.log("Config Data:"+configData.OAUTH_URL);
	console.log("Tenant:"+tenant);
	console.log("User:"+username);
	console.log("Password:"+password);

	var  axiosConfig = {
	  headers: {
	      "Access-Control-Allow-Origin": "*",
		  "Content-Type": "application/x-www-form-urlencoded"
	  },
	  auth: {
	      username: 'ssp_domain',
	      password: 'password'
	  }
	};

	var formData = new URLSearchParams();
	  formData.append('grant_type', 'password');
	  formData.append('username', tenant+"_"+username);
	  formData.append('password', password);

	console.log("formData:"+formData);

    return axios
      .post(configData.OAUTH_URL + "token", formData ,axiosConfig )
      .then(response => {
        if (response.data) {
          console.log("User Stored:"+response.data);
          localStorage.setItem('user', JSON.stringify(response.data+'}'));
        }

        return JSON.stringify(response.data);
      });
  }

  logout() {
    console.log("Remove user from store");
    localStorage.removeItem("user");
  }

  getCurrentUser() { 
    console.log("User:"+localStorage.getItem('user'));
	var authObj = JSON.parse(localStorage.getItem('user'));
    return authObj;
  }
}

export default new AuthService();
